def hide():
    print('w2_support.hide')
    sys.stdout.flush()
    w1.withdraw()                 # Causes the first toplevel to disappear.
    w.Button3.configure(state='active')
    w.Button2.configure(state='disabled')

def show():
    print('w2_support.show')
    sys.stdout.flush()
    w1.deiconify()                # Causes the first toplevel to reappear.
    w.Button3.configure(state='disabled')
    w.Button2.configure(state='active')

def close():
    print('w2_support.close')
    sys.stdout.flush()
